import React from 'react';
import { Bar } from 'react-chartjs-2'

export default function BarChart(props) {
    const parseData = data => ({
        labels: ['Temperature', 'Pressure', 'Humidity', 'Light', 'Noise', 'Gas'],
        datasets: [
            {
                label: 'Bar Chart',
                backgroundColor: [
                    'rgba(255,99,132,0,2)',
                    'rgba(54,162,235,0,2)',
                    'rgba(255,206,86,0,2)',
                    'rgba(255,99,132,0,2)',
                    'rgba(54,162,235,0,2)',
                    'rgba(255,206,86,0,2)'
                ],
                borderColor: [
                    'rgba(255,99,132,0,2)',
                    'rgba(54,162,235,0,2)',
                    'rgba(255,206,86,0,2)',
                    'rgba(255,99,132,0,2)',
                    'rgba(54,162,235,0,2)',
                    'rgba(255,206,86,0,2)'
                ],
                borderWidth: 5,
                hoverBackgroundColor: 'rgba(255,99,132,0.4)',
                hoverBorderColor: 'rgba(255,99,132,1)',
                data
            }
        ]
    })

    const options = {
        scales: {
            yAxes: [{
                ticks: {
                    beginAtZero: true
                }
            }]
        },
        maintainAspectRatio: false
    }

    return (
        <div>
            <Bar
                data={parseData(props.data)}
                option={options}
            />
        </div>
    )
}
