import React from 'react';
import { Line } from 'react-chartjs-2'

export default class LineChart extends React.Component {
    render() {
        console.log(Object.values(this.props.data));
        console.log(this.props.time);
        function processingData(rawData) {
            if (rawData) {
                parseData([].push(rawData[0]), (new Date).toTimeString())
            }
        }

        const parseData = (data, time) => ({
            labels: time,
            datasets: [{
                label: "Temperature Overtime",
                fill: false,
                backgroundColor: "rgba(75,192,192,0.4)",
                borderColor: "rgba(75,192,192,1)",
                hoverBackgroundColor: 'rgba(255,99,132,0.4)',
                hoverBorderColor: 'rgba(255,99,132,1)',
                data
            }]
        })

        const options = {
            scales: {
                yAxes: [{
                    ticks: {
                        beginAtZero: true
                    }
                }]
            },
            maintainAspectRatio: false
        }

        return (
            <Line
                // data={parseData(Object.values(this.props.data), this.props.time)}
                data={processingData(Object.values(this.props.data))}
                option={options}
            />
        )
    }
}
