import React, { Component, Fragment } from 'react';
import axios from 'axios';
import Pusher from 'pusher-js';
import Layout from '../components/Layout';
import BarChart from '../components/BarChart'

const pusher = new Pusher({
  appId: process.env.PUSHER_APP_ID,
  key: process.env.PUSHER_APP_KEY,
  secret: process.env.PUSHER_APP_SECRET,
  cluster: process.env.PUSHER_APP_CLUSTER,
  encrypted: true
});

const channel = pusher.subscribe('bar')

class IndexPage extends Component {
  constructor (props) {
    super(props)
    this.state = {
      data: [0, 0, 0, 0, 0, 0]
    }
  }

  componentDidMount () {
    this.receiveUpdateFromPusher()
  }

  receiveUpdateFromPusher () {
    channel.bind('bar-stats', data => {
      this.setState({data})
    })
    console.log('Bar app subbed to channel');
  } 

  render() {
    return (
      <Layout pageTitle="Realtime Data Visualization">
        <body className="container-fluid wf">

          <div className="row rw1">
            <div className="col-sm-2">
              Image Stream
            <div className="row">Img 1</div>
              <div className="row">Img 2</div>
              <div className="row">Img 3</div>
            </div>
            <div className="col-8 col-sm-6 wf">Video Stream</div>
            <div className="col wf">Graphs</div>
          </div>

          <div className="row rw2">
            <div className="col wf">Table</div>
            <div className="col wf">Bar Chart
              <BarChart data={this.state.data}/>
            </div>
            <div className="col wf">Graph</div>
          </div>
        </body>
      </Layout>
    );
  }

}

export default () => (
  <Fragment>
    <IndexPage />
  </Fragment>
);
