import React, { Component, Fragment } from 'react';
import axios from 'axios';
// import Pusher from 'pusher-js';
import Layout from '../components/Layout';
import BarChart from '../components/Bar'

const config = require('../config.json');
const PubNub = require('pubnub');

// const pusher = new Pusher('1045070', {
//   cluster: "ap4",
//   encrypted: true
// });

// const channel = pusher.subscribe('demo-stats');

class IndexPage extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      data: [1, 2, 1, 2, 1, 2]
    }
  }

  receiveUpdateFromPusher() {
    // channel.bind('bar-stats', data => {
    //   console.log(data);
    //   this.setState(
    //     data
    //   );
    //   console.log(binded);
    // })
    // console.log('Bar app subbed to channel');
    this.pubnub = new PubNub({
      subscirbeKey: config.subscribeKey
    });

    this.pubnub.subscribe({
      channels: ['bar']
    });

    this.pubnub.addListener({
      message: ({ data }) => {
        console.log(data)
        this.setState(data)
      }
    })
  } 

  componentWillUnmount() {
    this.pubnub.unsubscribe();
  }

  
  componentDidMount () {
    console.log('Before binded');
    this.receiveUpdateFromPusher()
  }

  render() {
    return (
      <Layout pageTitle="Realtime Data Visualization">
        <main className="container-fluid wf">

          <div className="row rw1">
            <div className="col-sm-2">
              Image Stream
            <div className="row">Img 1</div>
              <div className="row">Img 2</div>
              <div className="row">Img 3</div>
            </div>
            <div className="col-8 col-sm-6 wf">Video Stream</div>
            <div className="col wf">Graphs</div>
          </div>

          <div className="row rw2">
            <div className="col wf">Table</div>
            <div className="col wf">Bar Chart
              <BarChart data={this.state.data}/>
            </div>
            <div className="col wf">Graph</div>
          </div>
        </main>
      </Layout>
    );
  }

}

export default IndexPage;
